// export default Templates;
import React, { useEffect, useState } from "react";
import {
  Container,
  Card,
  Button,
  Table,
  Badge,
  Modal,
  Form,
  InputGroup,
  Row,
  Col,
} from "react-bootstrap";
import { FaPlus, FaEdit, FaTrash, FaSearch } from "react-icons/fa";
import axios from "axios";

const Templates = () => {
  const [templates, setTemplates] = useState([]);
  const [categories, setCategories] = useState([]);
  const [politicians, setPoliticians] = useState([]);
  const [religious, setReligious] = useState([]);

  const [showModal, setShowModal] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const [form, setForm] = useState({
    title: "",
    type: "",
    parentType: "",
    parentId: "",
    position: "",
    color: "",
    frame: "",
    image: null,
  });

  const API_URL = "https://shreejighutargo21.onrender.com/api";

  // Fetch all data
  useEffect(() => {
    fetchTemplates();
    fetchCategories();
    fetchPoliticians();
    fetchReligious();
  }, []);

  const fetchTemplates = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/template`);
      setTemplates(data.templates || []);
    } catch (err) {
      console.error("Error fetching templates:", err);
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/categories`);
      setCategories(data.categories || []);
    } catch (err) {
      console.error("Error fetching categories:", err);
    }
  };

  const fetchPoliticians = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/politicians`);
      setPoliticians(data.politicians || []);
    } catch (err) {
      console.error("Error fetching politicians:", err);
    }
  };

  const fetchReligious = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/religious`);
      setReligious(data.religious || []);
    } catch (err) {
      console.error("Error fetching religious:", err);
    }
  };

  const handleShow = () => {
    setEditingTemplate(null);
    setForm({
      title: "",
      type: "",
      parentType: "",
      parentId: "",
      position: "",
      color: "",
      frame: "",
      image: null,
    });
    setShowModal(true);
  };

  const handleClose = () => setShowModal(false);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setForm((prev) => ({ ...prev, [name]: files[0] }));
    } else {
      setForm((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(form).forEach((key) => {
      formData.append(key, form[key]);
    });

    try {
      if (editingTemplate) {
        await axios.put(`${API_URL}/template/${editingTemplate._id}`, formData);
      } else {
        await axios.post(`${API_URL}/template`, formData);
      }

      fetchTemplates();
      handleClose();
    } catch (err) {
      console.error("Error saving template:", err);
    }
  };

  const handleEdit = (template) => {
    setEditingTemplate(template);
    setForm({
      title: template.title,
      type: template.type,
      parentType: template.parentType,
      parentId: template.parentId,
      position: template.position,
      color: template.color,
      frame: template.frame,
      image: null,
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this template?")) {
      try {
        await axios.delete(`${API_URL}/template/${id}`);
        fetchTemplates();
      } catch (err) {
        console.error("Error deleting template:", err);
      }
    }
  };

  const filteredTemplates = templates.filter((t) =>
    t.title?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Safe parent options
  const parentOptions =
    form.parentType === "Category"
      ? categories
      : form.parentType === "Politician"
      ? politicians
      : religious;

  const safeParentOptions = Array.isArray(parentOptions) ? parentOptions : [];

  return (
    <Container fluid className="mt-4">
      <Card className="shadow-sm">
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h5 className="m-0">Templates</h5>
          <div className="d-flex gap-2">
            <InputGroup>
              <Form.Control
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <Button variant="outline-secondary">
                <FaSearch />
              </Button>
            </InputGroup>
            <Button variant="primary" onClick={handleShow}>
              <FaPlus /> Add Template
            </Button>
          </div>
        </Card.Header>

        <Card.Body>
          <Table bordered hover responsive>
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Type</th>
                <th>Parent Type</th>
                <th>Parent</th>
                <th>Position</th>
                <th>Frame</th>
                <th>Color</th>
                <th>Image</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredTemplates.map((t, i) => (
                <tr key={t._id}>
                  <td>{i + 1}</td>
                  <td>{t.title}</td>
                  <td>
                    <Badge bg="info">{t.type}</Badge>
                  </td>
                  <td>{t.parentType}</td>
                  <td>{t.parentName}</td>
                  <td>{t.position}</td>
                  <td>{t.frame}</td>
                  <td>
                    <div
                      style={{
                        backgroundColor: t.color,
                        width: "30px",
                        height: "20px",
                        borderRadius: "4px",
                      }}
                    ></div>
                  </td>
                  <td>
                    {t.image && (
                      <img
                        src={t.image}
                        alt="template"
                        width={50}
                        height={40}
                        style={{ objectFit: "cover", borderRadius: "6px" }}
                      />
                    )}
                  </td>
                  <td>
                    <Button
                      variant="warning"
                      size="sm"
                      onClick={() => handleEdit(t)}
                    >
                      <FaEdit />
                    </Button>{" "}
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleDelete(t._id)}
                    >
                      <FaTrash />
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      {/* Modal */}
      <Modal show={showModal} onHide={handleClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            {editingTemplate ? "Edit Template" : "Add Template"}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Title</Form.Label>
                  <Form.Control
                    type="text"
                    name="title"
                    value={form.title}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Type</Form.Label>
                  <Form.Select
                    name="type"
                    value={form.type}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Type</option>
                    <option value="Image">Image</option>
                    <option value="Video">Video</option>
                    <option value="Frame">Frame</option>
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Parent Type</Form.Label>
                  <Form.Select
                    name="parentType"
                    value={form.parentType}
                    onChange={handleChange}
                  >
                    <option value="">Choose</option>
                    <option value="Category">Category</option>
                    <option value="Politician">Politician</option>
                    <option value="Religious">Religious</option>
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Parent</Form.Label>
                  <Form.Select
                    name="parentId"
                    value={form.parentId}
                    onChange={handleChange}
                  >
                    <option value="">Choose</option>
                    {safeParentOptions.map((p) => (
                      <option key={p._id} value={p._id}>
                        {p.name || p.title}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Position</Form.Label>
                  <Form.Control
                    type="text"
                    name="position"
                    value={form.position}
                    onChange={handleChange}
                  />
                </Form.Group>
              </Col>

              <Col md={3}>
                <Form.Group className="mb-3">
                  <Form.Label>Frame</Form.Label>
                  <Form.Control
                    type="text"
                    name="frame"
                    value={form.frame}
                    onChange={handleChange}
                  />
                </Form.Group>
              </Col>

              <Col md={3}>
                <Form.Group className="mb-3">
                  <Form.Label>Color</Form.Label>
                  <Form.Control
                    type="color"
                    name="color"
                    value={form.color}
                    onChange={handleChange}
                  />
                </Form.Group>
              </Col>

              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Image</Form.Label>
                  <Form.Control
                    type="file"
                    name="image"
                    onChange={handleChange}
                    accept="image/*"
                  />
                </Form.Group>
              </Col>
            </Row>
            <Button type="submit" variant="success" className="w-100">
              {editingTemplate ? "Update Template" : "Add Template"}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Templates;
