import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Card, Form, Button, Alert, Table } from "react-bootstrap";

const BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:7000";

const Reports = () => {
  const [form, setForm] = useState({ email: "", description: "" });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [reports, setReports] = useState([]);

  // Fetch all reports
  const fetchReports = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/reports`);
      setReports(res.data.data || []);
    } catch (err) {
      console.error("Fetch reports error:", err);
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.email || !form.description) {
      setMessage("Please fill all fields");
      return;
    }
    setLoading(true);
    try {
      await axios.post(`${BASE_URL}/api/reports`, form);
      setMessage("Report added successfully!");
      setForm({ email: "", description: "" });
      fetchReports();
    } catch (err) {
      setMessage("Error adding report");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container style={{ maxWidth: "800px", marginTop: "30px" }}>
      <Card className="shadow-sm p-4">
        <h3 className="mb-4">Add Report</h3>
        {message && <Alert variant="info">{message}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              placeholder="Enter email"
              value={form.email}
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="description"
              placeholder="Enter description"
              value={form.description}
              onChange={handleChange}
            />
          </Form.Group>

          <Button variant="primary" type="submit" disabled={loading}>
            {loading ? "Saving..." : "Save"}
          </Button>
        </Form>
      </Card>

      <Card className="shadow-sm p-4 mt-4">
        <h4>Saved Reports</h4>
        <Table bordered hover responsive className="mt-3">
          <thead>
            <tr>
              <th>Email</th>
              <th>Description</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {reports.length > 0 ? (
              reports.map((r) => (
                <tr key={r._id}>
                  <td>{r.email}</td>
                  <td>{r.description}</td>
                  <td>{new Date(r.createdAt).toLocaleString()}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="text-center">
                  No reports found
                </td>
              </tr>
            )}
          </tbody>
        </Table>
      </Card>
    </Container>
  );
};

export default Reports;
