import React, { useEffect, useState } from "react";
import {
  Container, Card, Button, Table, Badge, Modal, Form, InputGroup, Row, Col
} from "react-bootstrap";
import { FaPlus, FaEdit, FaTrash, FaSearch } from "react-icons/fa";
import axios from "axios";

const profilePositions = [
  "topLeft","topRight","bottomLeft","bottomRight","center",
  "centerLeft","centerRight","topCenter","bottomCenter"
];

const transitionTypes = [
  "fade","slideFromBottom","slideFromTop","slideFromLeft","slideFromRight",
  "scale","rotation","bounce","ripple"
];

const orientations = ["landscape","portrait"];
const placements = ["above","below"];
const profileShapes = ["circle","square","rectangle"];

const Templates = () => {
  const [templates, setTemplates] = useState([]);
  const [categories, setCategories] = useState([]);
  const [politicians, setPoliticians] = useState([]);
  const [religious, setReligious] = useState([]);

  const [show, setShow] = useState(false);
  const [editId, setEditId] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [framePreview, setFramePreview] = useState(null);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    title: "",
    type: "video",
    status: "active",
    parentType: "Category",
    parentId: "",
    file: null,
    transitionPlacement: "below",
    profilePosition: "center",
    transitionType: "fade",
    orientation: "landscape",
    profileSize: "",
    profileShape: "circle",
    frameFile: null,
  });

  const BASE_URL = "https://clonecraftbackend-gydw.vercel.app/api";

  // âœ… safe getter
  const safeArray = (data) => (Array.isArray(data) ? data : []);

  // ðŸŸ¢ Fetch all data
  const fetchTemplates = async () => {
    try {
      const { data } = await axios.get(`${BASE_URL}/templates`);
      setTemplates(safeArray(data.templates || data));
    } catch (err) {
      console.error("Fetch templates error:", err);
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await axios.get(`${BASE_URL}/categories`);
      setCategories(safeArray(data.categories || data));
    } catch (err) {
      console.error("Fetch categories error:", err);
    }
  };

  const fetchPoliticians = async () => {
    try {
      const { data } = await axios.get(`${BASE_URL}/politicians`);
      setPoliticians(safeArray(data.politicians || data));
    } catch (err) {
      console.error("Fetch politicians error:", err);
    }
  };

  const fetchReligious = async () => {
    try {
      const { data } = await axios.get(`${BASE_URL}/religious`);
      setReligious(safeArray(data.religious || data));
    } catch (err) {
      console.error("Fetch religious error:", err);
    }
  };

  useEffect(() => {
    fetchTemplates();
    fetchCategories();
    fetchPoliticians();
    fetchReligious();
  }, []);

  // ðŸŸ¢ Modal open
  const handleShow = (tpl = null) => {
    if (tpl) {
      setEditId(tpl._id);
      setForm({
        title: tpl.title || "",
        type: tpl.type || "video",
        status: tpl.status || "active",
        parentType: tpl.category
          ? "Category"
          : tpl.politician
          ? "Politician"
          : "Religious",
        parentId:
          tpl.category?._id || tpl.politician?._id || tpl.religious?._id || "",
        file: null,
        transitionPlacement: tpl.transitionPlacement || "below",
        profilePosition: tpl.profilePosition || "center",
        transitionType: tpl.transitionType || "fade",
        orientation: tpl.orientation || "landscape",
        profileSize: tpl.profileSize || "",
        profileShape: tpl.profileShape || "circle",
        frameFile: null,
      });
      setFilePreview(tpl.file || null);
      setFramePreview(tpl.frameFile || null);
    } else {
      setEditId(null);
      setForm({
        title: "",
        type: "video",
        status: "active",
        parentType: "Category",
        parentId: "",
        file: null,
        transitionPlacement: "below",
        profilePosition: "center",
        transitionType: "fade",
        orientation: "landscape",
        profileSize: "",
        profileShape: "circle",
        frameFile: null,
      });
      setFilePreview(null);
      setFramePreview(null);
    }
    setShow(true);
  };

  const handleClose = () => setShow(false);

  // ðŸŸ¢ Form change handler
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "file" || name === "frameFile") {
      const selectedFile = files && files[0];
      if (!selectedFile) return;
      setForm((f) => ({ ...f, [name]: selectedFile }));
      if (name === "file") setFilePreview(URL.createObjectURL(selectedFile));
      else setFramePreview(URL.createObjectURL(selectedFile));
    } else {
      setForm((f) => ({ ...f, [name]: value }));
    }
  };

  // ðŸŸ¢ Submit handler
  const handleSubmit = async () => {
    if (!form.title || !form.parentId)
      return alert("Title & Parent required!");
    setLoading(true);
    try {
      const fd = new FormData();
      fd.append("title", form.title);
      fd.append("type", form.type);
      fd.append("status", form.status);
      fd.append("orientation", form.orientation);
      fd.append("transitionPlacement", form.transitionPlacement);
      fd.append("profileSize", form.profileSize);
      fd.append("profileShape", form.profileShape);
      fd.append("profilePosition", form.profilePosition);

      if (form.parentType === "Category")
        fd.append("category", form.parentId);
      else if (form.parentType === "Politician")
        fd.append("politician", form.parentId);
      else fd.append("religious", form.parentId);

      if (form.type === "video")
        fd.append("transitionType", form.transitionType);

      if (form.file) fd.append("file", form.file);
      if (form.frameFile) fd.append("frameFile", form.frameFile);

      const url = editId
        ? `${BASE_URL}/templates/${editId}`
        : `${BASE_URL}/templates`;
      const method = editId ? "put" : "post";
      await axios[method](url, fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      await fetchTemplates();
      setShow(false);
    } catch (err) {
      console.error("Submit error:", err);
      alert(err.response?.data?.message || "Error saving template");
    } finally {
      setLoading(false);
    }
  };

  // ðŸŸ¢ Delete
  const handleDelete = async (id) => {
    if (!window.confirm("Delete this template?")) return;
    try {
      await axios.delete(`${BASE_URL}/templates/${id}`);
      fetchTemplates();
    } catch (err) {
      console.error("Delete error:", err);
      alert("Failed to delete template");
    }
  };

  // ðŸŸ¢ Filter
  const filtered = templates.filter(
    (t) =>
      t.title?.toLowerCase().includes(search.toLowerCase()) ||
      t.type?.toLowerCase().includes(search.toLowerCase())
  );

  // âœ… Parent dropdown safe handling
  const parentOptions = Array.isArray(
    form.parentType === "Category"
      ? categories
      : form.parentType === "Politician"
      ? politicians
      : religious
  )
    ? form.parentType === "Category"
      ? categories
      : form.parentType === "Politician"
      ? politicians
      : religious
    : [];

  return (
    <Container fluid>
      <Row className="align-items-center mb-3">
        <Col>
          <h2>Templates</h2>
        </Col>
        <Col md="5">
          <InputGroup>
            <Form.Control
              placeholder="Search by title or type..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <Button variant="outline-secondary">
              <FaSearch />
            </Button>
          </InputGroup>
        </Col>
        <Col className="text-end">
          <Button onClick={() => handleShow()}>
            <FaPlus className="me-2" />
            Add Template
          </Button>
        </Col>
      </Row>

      <Card className="mb-4">
        <Card.Body>
          <Table responsive hover className="align-middle">
            <thead>
              <tr>
                <th>Thumbnail</th>
                <th>Title</th>
                <th>Type</th>
                <th>Parent</th>
                <th>Profile Position</th>
                <th>Profile Size</th>
                <th>Shape</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((tpl) => (
                <tr key={tpl._id}>
                  <td style={{ width: 100 }}>
                    {tpl.file ? (
                      tpl.type === "video" ? (
                        <video
                          src={tpl.file}
                          width="120"
                          height="70"
                          style={{ objectFit: "cover" }}
                        />
                      ) : (
                        <img
                          src={tpl.file}
                          alt={tpl.title}
                          width="120"
                          height="70"
                          style={{
                            objectFit: "cover",
                            borderRadius: 6,
                          }}
                        />
                      )
                    ) : (
                      <div
                        style={{
                          width: 120,
                          height: 70,
                          background: "#f0f0f0",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          color: "#888",
                          borderRadius: 6,
                        }}
                      >
                        No file
                      </div>
                    )}
                  </td>
                  <td>{tpl.title}</td>
                  <td>
                    <Badge
                      bg={tpl.type === "video" ? "primary" : "success"}
                    >
                      {tpl.type}
                    </Badge>
                  </td>
                  <td>
                    {tpl.category?.name ||
                      tpl.politician?.title ||
                      tpl.religious?.title ||
                      "-"}
                  </td>
                  <td>{tpl.profilePosition}</td>
                  <td>{tpl.profileSize || "-"}</td>
                  <td>{tpl.profileShape || "-"}</td>
                  <td>
                    <Badge
                      bg={tpl.status === "active" ? "success" : "secondary"}
                    >
                      {tpl.status}
                    </Badge>
                  </td>
                  <td>
                    <Button
                      size="sm"
                      variant="outline-primary"
                      className="me-2"
                      onClick={() => handleShow(tpl)}
                    >
                      <FaEdit />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline-danger"
                      onClick={() => handleDelete(tpl._id)}
                    >
                      <FaTrash />
                    </Button>
                  </td>
                </tr>
              ))}
              {filtered.length === 0 && (
                <tr>
                  <td colSpan="9" className="text-center text-muted">
                    No templates found
                  </td>
                </tr>
              )}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      {/* ðŸ”µ Modal */}
      <Modal show={show} onHide={handleClose} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            {editId ? "Edit Template" : "Add Template"}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {/* Basic Fields */}
            <Row>
              <Col md={8}>
                <Form.Group className="mb-3">
                  <Form.Label>Title</Form.Label>
                  <Form.Control
                    name="title"
                    value={form.title}
                    onChange={handleChange}
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Type</Form.Label>
                  <Form.Select
                    name="type"
                    value={form.type}
                    onChange={handleChange}
                  >
                    <option value="video">Video</option>
                    <option value="graphics">Graphics</option>
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            {/* Parent Select */}
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Parent Type</Form.Label>
                  <Form.Select
                    name="parentType"
                    value={form.parentType}
                    onChange={handleChange}
                  >
                    <option value="Category">Category</option>
                    <option value="Politician">Politician</option>
                    <option value="Religious">Religious</option>
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>
                    Select {form.parentType}
                  </Form.Label>
                  <Form.Select
                    name="parentId"
                    value={form.parentId}
                    onChange={handleChange}
                  >
                    <option value="">Choose</option>
                    {parentOptions.map((p) => (
                      <option key={p._id} value={p._id}>
                        {p.name || p.title}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            {/* Orientation + Placement */}
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Transition Placement</Form.Label>
                  <Form.Select
                    name="transitionPlacement"
                    value={form.transitionPlacement}
                    onChange={handleChange}
                  >
                    {placements.map((p) => (
                      <option key={p} value={p}>
                        {p}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Orientation</Form.Label>
                  <Form.Select
                    name="orientation"
                    value={form.orientation}
                    onChange={handleChange}
                  >
                    {orientations.map((o) => (
                      <option key={o} value={o}>
                        {o}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            {/* Profile & Transition */}
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Profile Position</Form.Label>
                  <Form.Select
                    name="profilePosition"
                    value={form.profilePosition}
                    onChange={handleChange}
                  >
                    {profilePositions.map((p) => (
                      <option key={p} value={p}>
                        {p}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>

              <Col md={6}>
                {form.type === "video" && (
                  <Form.Group className="mb-3">
                    <Form.Label>Transition Type</Form.Label>
                    <Form.Select
                      name="transitionType"
                      value={form.transitionType}
                      onChange={handleChange}
                    >
                      {transitionTypes.map((t) => (
                        <option key={t} value={t}>
                          {t}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                )}
              </Col>
            </Row>

            {/* Size + Shape */}
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Profile Size</Form.Label>
                  <Form.Control
                    name="profileSize"
                    value={form.profileSize}
                    onChange={handleChange}
                    placeholder="e.g. 300x300"
                  />
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Profile Shape</Form.Label>
                  <Form.Select
                    name="profileShape"
                    value={form.profileShape}
                    onChange={handleChange}
                  >
                    {profileShapes.map((shape) => (
                      <option key={shape} value={shape}>
                        {shape}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            {/* Uploads */}
            <Form.Group className="mb-3">
              <Form.Label>Frame Image Upload</Form.Label>
              <Form.Control
                type="file"
                name="frameFile"
                onChange={handleChange}
              />
              {framePreview && (
                <img
                  src={framePreview}
                  alt="frame"
                  width="100%"
                  className="mt-2"
                />
              )}
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Template File (image or video)</Form.Label>
              <Form.Control
                type="file"
                name="file"
                onChange={handleChange}
              />
              {filePreview &&
                (form.type === "video" ? (
                  <video
                    src={filePreview}
                    controls
                    width="100%"
                    className="mt-2"
                  />
                ) : (
                  <img
                    src={filePreview}
                    alt="preview"
                    width="100%"
                    className="mt-2"
                  />
                ))}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading ? "Saving..." : editId ? "Update" : "Save"}
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Templates;
